mapper.insert:

    SELECT LAST_INSERT_ID() as id

INSERT INTO cart (


        ,uid


        ,gid


        ,spec


        ,amount

    ,created_at
    ,updated_at

) VALUES (


        ,#{uid}


        ,#{gid}


        ,#{spec}


        ,#{amount}

    ,now()
    ,now()

)

mapper.delete:
DELETE FROM cart WHERE
gid=#{gid}
AND spec=#{spec}
