mapper.select:
SELECT count(1) FROM items LEFT JOIN item_spec ON items.id=item_spec.gid
WHERE items.id=#{id}
AND item_spec.id=#{spec}
AND items.is_online = 1
AND item_spec.is_online = 1

SELECT id, no, name, cid1, cid2, is_online, src, thumb, place, summary, notice, notice_color, description, created_at, updated_at, deleted_at FROM items WHERE is_online = 1
and cid1=#{cid1}
and cid2=#{cid2}

SELECT count(1) FROM items
WHERE 1=1

    and cid1=#{cid1}


    and cid2=#{cid2}

mapper.insert:

    SELECT LAST_INSERT_ID() as id

INSERT INTO items (


        ,no


        ,name


        ,cid1


        ,cid2


        ,is_online



        ,src


        ,thumb


        ,place


        ,summary


        ,notice


        ,notice_color


        ,description

    ,created_at
    ,updated_at

) VALUES (


        ,#{no}


        ,#{name}


        ,#{cid1}


        ,#{cid2}


        ,#{is_online}


        ,#{src}


        ,#{thumb}


        ,#{place}


        ,#{summary}


        ,#{notice}


        ,#{notice_color}


        ,#{description}

    ,now()
    ,now()

)
