mapper.select:
SELECT count(1) FROM orders
WHERE order_status NOT IN (200,500)

    AND  created_at >=#{start,javaType=Date } 


    AND  created_at <=#{end,javaType=Date }

SELECT id, NO, uid, confirmed, order_status, pay_status, ship_status, package_status, order_total, order_amount, order_weight, order_money, order_amount_real, order_money_real, bid, bonus, point, point_used, pid, payment, send_date, time_start, time_end, address_id, mobile, province, receiver, city, district, road, address, message, prepay_id, created_at, updated_at, deleted_at FROM orders where 1=1
and order_status=#{order_status}
and pay_status=#{pay_status}
and pay_status=#{pay_status}
and ship_status=#{ship_status}
AND created_at >=#{created_at_start,javaType=Date }
AND created_at <= #{created_at_end,javaType=Date }
AND send_date >=#{time_start,javaType=Date,jdbcType=DATE }
AND send_date <= #{time_end,javaType=Date,jdbcType=DATE }
AND time_start >=#{time_start,javaType=Date,jdbcType=TIMESTAMP }
AND time_end <= #{time_end,javaType=Date,jdbcType=TIMESTAMP }
ORDER by created_at DESC

SELECT id, NO, uid, confirmed, order_status, pay_status, ship_status, package_status, order_total, order_amount, order_weight, order_money, order_amount_real, order_money_real, bid, bonus, point, point_used, pid, payment, send_date, time_start, time_end, address_id, mobile, receiver, province, city, district, road, address, message, prepay_id, created_at, updated_at, deleted_at FROM orders where uid=#{uid}
AND order_status IN
#{order_status_item}
AND pay_status IN
#{pay_status_item}
AND ship_status IN
#{ship_status_item}
AND package_status IN
#{package_status_item}
ORDER by created_at DESC

SELECT count(1) FROM orders
where uid=#{uid}

    AND order_status IN

        #{order_status_item}



    AND pay_status IN

        #{pay_status_item}



    AND ship_status IN

        #{ship_status_item}



    AND package_status IN

        #{package_status_item}

mapper.insert:

    SELECT LAST_INSERT_ID() as id

